INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1047, NOW(), 338, 'Vitensenteret - Publikumsvert - Deltid/Sommerjobb', 'Er du litt nerdete, interessert i vitenskap og glad i å snakke foran mennesker? Eller har du en lidenskap for eksplosjoner og spenstige kjemiske reaksjoner?', '**Vitenpilot (vitenshow)**

