INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (684, NOW(), 234, 'Stillingsannonse hos Sticos', 'Vil du bli en av tre studenter i en tverrfaglig Sticos Summer Squad? Er du klar for å prøve ut det du har lært så langt å designe og bygge software? Er du engasjert, nysgjerrig og ikke redd for å hive deg ut i nye utfordringer?', 'Vil du bli en av tre studenter i en tverrfaglig Sticos Summer Squad? Er du klar for å prøve ut det du har lært så langt å designe og bygge software? Er du engasjert, nysgjerrig og ikke redd for å hive deg ut i nye utfordringer? Da synes vi du skal søke sommerjobb hos oss!

