INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (686, NOW(), 236, 'Sommerjobb hos Cimon Medical', 'Vi bidrar til å redusere risiko for hjerneskade hos spedbarn gjennom ultralydbasert overvåkningsteknologi', E'**Vi tilbyr følgende sommerjobber:**

