INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (687, NOW(), 192, 'Summer internship 2022 - Cloud Database team', 'We are currently searching for candidates for our summer internship 2022, Cloud Database
team in Trondheim. This is a great opportunity to join and get to know a world leading Tech
