INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1025, NOW(), 181, 'Summer Internship 2025', 'Ønsker du å skape meningsfull teknologi? Og er nysgjerrig på konsulentlivet? Da er et summer internship i Tietoevry Create noe for deg!', '**Ønsker du å skape meningsfull teknologi? Og er nysgjerrig på konsulentlivet? Da er et summer internship i Tietoevry Create noe for deg!**

