INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1038, NOW(), 314, 'Development Team - Cloud & Apps', 'Kunne du tenke deg en betalt og fleksibel deltidsstilling i et team
