INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (710, NOW(), 7, 'Summer internship 2022 - Oslo', 'Vil du teste ut livet som konsulent hos oss? I løpet av seks uker vil du jobbe med ekte prosjekter i tverrfaglige team bestående av andre studenter, veiledere og prosjektledere.', 'Vil du teste ut livet som konsulent hos oss? I løpet av seks uker vil du jobbe med ekte prosjekter i tverrfaglige team bestående av andre studenter, veiledere og prosjektledere. Du vil få et unikt innblikk i hverdagen hos et av verdens ledende konsulentselskaper og uvurderlig arbeidserfaring. Hos oss er det viktig å ha det gøy på jobb, derfor er sosiale aktiviteter en viktig del av programmet.   

