INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1001, NOW(), 16, 'Sommerjobb Oslo 2025', '*Ønsker du deg en sommerjobb med Norges fremste ingeniører innen sikkerhet og nettverkskommunikasjon?*', 'Vi søker dyktige og motiverte studenter som er ferdig med 3. eller 4. klasse og som kan vise til gode akademiske resultater. Hos oss får du jobbe med reelle problemstillinger, individuelt eller i små team, og du får erfaring med avansert teknologi og unike produktområder.

