INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (694, NOW(), 240, 'Jobbe med sportsteknologi?', 'Tidlig i 2022 ruller vi ut første utgave av vår programvare og i den anledning ønsker vi bygge et større
tech-team, med deg på laget.', 'Vi er et voksende oppstartsselskap fra NTNU som lager software for bevegelsesanalyse. Dette gjør vi fordi det i dag ikke eksisterer noen gode måter å skaffe objektiv kunnskap om menneskelig motorikk på. Med bruk av maskinlæring lager vi en løsning for å gi folk objektive data om individuelle bevegelsesmønster og hvordan dette påvirker fysisk helse på en enkel og smooth måte.

