INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (715, NOW(), 157, 'Coop teknisk trainee 2022', 'Vil du utvikle deg stort, er det lurt å velge en bransje som utvikler seg enda mer!', 'Som teknisk trainee i Coop Norge vil du ta del i et skreddersydd program der du over to år skal fordype deg i grensesnittet mellom det mest komplette ERP-systemet i markedet og de mange ulike forretningsprosessene i Coop. Oppstart for programmet er i august 2022.

