INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (717, NOW(), 118, 'Nyutdannede utviklere', 'Vi søker deg som har lyst å jobbe med viktige løsninger som folk bruker i det daglige.', 'Du vil delta i et unikt opplæringsløp, hvor du jobber i et tverrfaglig team sammen med både seniorkonsulenter og andre nyutdannede. Målet er at du skal lære mye om metodikk og teknologi på kort tid. Du vil få tett oppfølging av erfarne og dyktige fagfolk underveis. I tillegg har vi stort fokus på de sosiale sidene ved arbeidslivet, med mange artige aktiviteter. Man lærer bedre når man har det gøy sammen med andre.

