INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (727, NOW(), 48, 'ITverket søker nyutdannede', 'Ferdig med studiene snart? ITverket søker nyutdannede for oppstart høsten 2022', 'ITverket er et konsulentselskap lokalisert i Oslo og Drammen med 116 engasjerte
