INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (721, NOW(), 17, 'EY Skye Technology Graduate', 'Ønsker du en spennende jobb med utviklingsmuligheter?', 'EY Skye er et teknologiselskap som kombinerer dyp teknologikompetanse med forretningsutvikling.

