INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (760, NOW(), 260, 'Analytics-konsulent i traineeprogrammet Nytt Krutt', 'Kickstart din karriere hos oss i Visma bWise!', 'I Visma bWise har vi det sterkeste fagmiljøet og leverer noen av de største og viktigste prosjektene i Norge innenfor Analytics og Business Intelligence (BI). Vi skaper innsikt hos virksomheter og i samfunnet gjennom avanserte analyser og løsninger. Hos oss vil du som kunne hjelpe Helsedirektoratet å få innsikt i deres tall, utvikle et statistikkverktøy for Utdanningsdirektoratet eller automatisk produserte rapporter for Lovisenberg Diakonale Sykehus. Vi tror på enkle løsninger med bruk av det nyeste innenfor teknologi, heller enn de store og kompliserte løsningene. Samtidig er vi landets fremste på Qlik-løsninger, samt Tableau og Power BI.

Traineeprogrammet Nytt Krutt skal gjøre overgangen fra å være student til å bli konsulent så lærerik, trygg og tilrettelagt som mulig. Du får en bratt læringskurve og mange utfordringer. Samtidig jobber du i team med andre nyutdannede og våre erfarne konsulenter.

