INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (761, NOW(), 186, 'Datastudent? Jobb med oss sommeren 2023!', 'Vi ser etter flinke og hyggelige studenter som vil teste ut konsulentlivet hos oss mellom 26. juni og 04. august.', '[Se annonsen på fink.no.](https://www.fink.no/jobb/datastudent)

