INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (775, NOW(), 142, 'Sommerjobb hos Bredvid 2023', 'Vil du prøve deg som konsulent hos et ungt og spennende selskap? Bredvid søker studenter som ønsker erfaring om hvordan det er å være konsulent.', 'Som sommeransatt hos Bredvid vil du jobbe som utvikler på et reelt prosjekt hos en av våre kunder, med relevante teknologier og tett oppfølging fra konsulenter fra Bredvid. Prosjektet vil være av en slik størrelse at du som sommeransatt får følge det fra start til slutt. I løpet av sommeren vil du tilegne deg erfaring som utvikler, og som teammedlem i samarbeid med en ekstern kunde.  

