INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (770, NOW(), 7, 'Summer internship 2023 - Utviklere', 'Vil du teste ut livet som konsulent hos oss? I løpet av seks uker vil du jobbe med ekte prosjekter i tverrfaglige team. Du vil få et unikt innblikk i hverdagen hos et verdensledende konsulentselskap sat uvurderlig erfaring.', 'Vil du teste ut livet som konsulent hos oss? I løpet av seks uker vil du jobbe med ekte prosjekter i tverrfaglige team bestående av andre studenter, veiledere og prosjektledere. Du vil få et unikt innblikk i hverdagen hos et verdensledende konsulentselskap samt uvurderlig arbeidserfaring. Hos oss er det viktig å ha det gøy på jobb, derfor er sosiale aktiviteter en viktig og utbredt del av programmet.

