INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (776, NOW(), 8, 'Summer Internship 2023 - Netlight (Oslo)', 'Ønsker du å jobbe med den fremste teknologien sammen med Norges skarpeste og morsomste kollegaer? Da har du kommet til rett sted!', 'Netlight er et internasjonalt IT-konsulentselskap. Vi har hatt kontorer i Oslo siden 2008, og er i dag litt over 150 norske kollegaer. Våre ansatte er blant markedets største konsulenttalenter og våre kunder er markedsledende i sine innovative digitale løsninger. Som organisasjon bygger Netlight på tillit, ansvar og inkludering. Vi fokuserer på personlig utvikling kombinert med vekst av kompetanse og kunnskap. Vi deler kunnskap på kryss og tvers av nettverket og hjelper hverandre til å bli bedre. Som en Netlighter blir du gitt ansvar fra første sekund og du får muligheten til å være med å forme hvilket selskap Netlight skal være i fremtiden.


