INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (793, NOW(), 33, 'Vi søker morgendagens konsulentstjerner til vårt talentprogram 2023!', 'August-opptaket er nå avsluttet og vi er i gang med vårt september-opptak! Ny søknadsfrist er derfor 18.september. Vi har fremdeles løpende opptak og anbefaler deg å legge inn din søknad allerede nå.', '_Vår visjon er å utvikle markedets beste talenter. I løpet av vårt toårige talentprogram, videreutdanner og trener vi våre nyutdannede kontinuerlig i nye teknologier, konsulent-_ _og prosjektarbeid, samt generell forretningsvirksomhet._  
  
