INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (800, NOW(), 23, 'Systemutvikler sommerjobb dips', 'Vi er på utkikk etter studenter som ønsker sommerjobb i 2023.', 'Vil du være en del av våre innovative team, lære mye nytt og bidra til å skrive kode som betyr noe? Da må du lese videre!

