INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (808, NOW(), 252, 'Sommerjobb som utvikler hos en hemmelig tjeneste?', 'NSMs ønske er at dere får jobbe med et prosjekt dere synes er både spennende og
relevant. Prosjektet vil fokusere på å gjøre hverdagen til våre inntrengingstestere
enklere, ved å videreutvikle verktøy de bruker under inntrengingstester.', 'Nasjonal sikkerhetsmyndighet søker kreative og teknologiinteresserte studenter som
