INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (809, NOW(), 14, 'Sommerjobb for data scientists / data engineers  — Oslo', 'En relevant sommerjobb er gull verdt for deg som ønsker erfaring og muligheten til å sette teori i praksis. Jobb med erfarne Bekk-konsulenter på aktuelle oppdrag, lære masse og vis oss hva du kan – og kanskje leder det til en jobb etter studiene!', '## Hvem er du?
