INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (813, NOW(), 109, 'Tech Summer Internship 2023 - 8 uker med faglige utfordringer!', 'Hos oss bidrar du til å lage løsninger som er viktige for hele verdikjeden i NorgesGruppen og etter endt internship vil du sitte igjen med en innsikt og kompetanse om varehandel som vil gi deg et fortrinn videre i arbeidslivet.', 'Her får du som er IT- og datateknologistudent en fantastisk mulighet i ett av Norges største selskaper. Hos oss bidrar du til å lage løsninger som er viktige for hele verdikjeden i NorgesGruppen og etter endt internship vil du sitte igjen med en innsikt og kompetanse om varehandel som vil gi deg et fortrinn videre i arbeidslivet.

