INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (816, NOW(), 268, 'Summer Job 2023 - Norway', 'A summer in Nordic gives you insights within the tech industry, project work, teamwork, and new connections. Does this sound interesting?', 'Se annonsen på [candidate.hr-manager.net.](https://candidate.hr-manager.net/ApplicationInit.aspx?cid=278&ProjectId=177398&DepartmentId=7392&MediaId=5&SkipAdvertisement=False)

## Are you ready for a fun and challenging summer job?

