INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1052, NOW(), 341, 'Sommer-internship: Utvikler/AI/Optimering hos enmo', 'Vi søker en engasjert student med interesse for utvikling, AI eller optimering. Oppgaver tilpasses din motivasjon, og du får mulighet til å forme egne prosjekter.', 'Vi søker en engasjert student med interesse for utvikling, AI eller optimering. Oppgaver tilpasses din motivasjon, og du får mulighet til å forme egne prosjekter.

