INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1030, NOW(), 11, 'Energisommerjobb 2025', 'Nå kan du prøve deg som forsker for en sommer hos SINTEF, et av Europas største uavhengige forskningsinstitutt!', 'SINTEF Energi utvikler fremtidens bærekraftige energiløsninger. I tett samarbeid med NTNU, industri og næringsliv, forsker vi fram ny teknologi med visjonen teknologi for et bedre samfunn.

