INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1008, NOW(), 328, 'Deltidsjobb som leksehjelper', 'Vi søker etter to dedikerte studenter eller engasjerte leksehjelpere i matematikk som kan tilby 1-til-1 undervisning på nett til våre elever på ungdomsskolen og videregående nivå. Hvis du har en god forståelse for matematikk, brenner for å hjelpe and', 'Søknadsfrist: **Snarest**

