INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (837, NOW(), 277, 'Full Stack Web Developer - Summer Intern 2023', 'Do you enjoy developing web-based applications, using a range of technologies and platforms? We are looking for Full Stack Web Developer to join us as a summer intern in 2023!', 'https://www.blueyerobotics.com/page/full-stack-web-developer-summer-intern-2023

