INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1042, NOW(), 175, 'Fullstack-utvikler [2. - 4. klasse]', 'Vil du bygge ekte systemer og få verdifull erfaring under studiene?', 'Vil du bygge ekte systemer og få verdifull erfaring under studiene?

