INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (991, NOW(), 41, 'Sommerjobb for teknologi interesserte studenter!', 'Vi gir deg 6 uker på å lage en samfunnsnyttig løsning for fremtiden. Tar du utfordringen?', '# Sommerjobben

