INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1043, NOW(), 328, 'Mattehelten - Hjelp ungdommer med mattelekser på nett', 'Mattehelten søker etter to studenter som kan tilby 1-til-1 matteundervisning på nett til våre elever på ungdomsskolen og videregående nivå. Søk hvis du brenner for å hjelpe andre og kan formidle konsepter på en enkel og forståelig måte.', 'Vi søker etter to dedikerte studenter eller engasjerte leksehjelpere i matematikk som kan tilby 1-til-1 undervisning på nett til våre elever på ungdomsskolen og videregående nivå. Hvis du har en god forståelse for matematikk, brenner for å hjelpe andre og kan formidle konsepter på en enkel og forståelig måte, håper vi at du søker!

### 1) Hva er stillingen?
Planlegge og gjennomføre individuelle digitale leksehjelpøkter med elever.

Inspirere elever i matematikk for positiv holdning, mestring og glede.



