INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (846, NOW(), 55, 'Internal Communications Summer Internship - Collaboration Services', 'Collaboration Summer Internship', '**Collaboration & Productivity**  manage all of the Collaboration and productivity tools used in Schibsted, spanning from email and chat, to project planning as well as code repositories.

