INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (850, NOW(), 181, 'Summer internship', 'Kreativ? Introvert? Ekstrovert? Eller kanskje du er neste Steve Jobs? Bli med på Summer Academy hos oss i Tietoevry Create, så vil du bli med å lage teknologiske løsninger for fremtiden.', 'Tietoevry Create **Academy - Summer Internship** _(Trondheim)_

