INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (851, NOW(), 152, 'Sommerjobb som forskningsassistent 2023', 'Lærerik sommer ved Norsk Regnesentral', 'Norsk Regnesentral (NR) ser på deg som er student som en viktig fremtidig ressurs, og vil gjerne bli kjent med deg allerede i studietiden. Gjennom en sommerjobb hos oss får du en unik mulighet til å se hvordan vi jobber her ved NR, og samtidig knytte langsiktige kontakter. Du vil bli fulgt opp av våre erfarne forskere og får et realistisk innblikk i hvordan det er å jobbe på et forskningsinstitutt.

