INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (857, NOW(), 74, 'Utviklere søkes til NTNUs IT-avdeling', 'Ønsker du å jobbe med spennende oppgaver i et av Trondheims største IT-miljø?', '### Om stillingen
Ved Seksjon for IT-Utvikling er det ledig flere faste 100 % stillinger som systemutviklere.
