INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (861, NOW(), 285, 'Software Developer (Internship summer 2023) at H5P Group AS', 'Do you want to help students all over the world get a better education?', 'Do you want to help students all over the world get a better education? Do you want to develop the tools that your lecturer uses to create next generation interactive content for your class? Do you want to experience how it is working in a fast growing product-based start-up where everyone is passionate and proud of what they are creating?


