INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (859, NOW(), 125, 'Deltidsstilling som IT-, design eller strategikonsulent i Junior Consulting', 'Junior Consulting søker nye konsulenter!', 'Junior Consulting er et studentkonsulentselskap som er eid og drevet av studenter ved NTNU. Stillingene er åpne for alle studieretninger, og er tiltenkt studenter i 2.-4. klasse. Vi jobber mellom 5 og 10 timer i uken, både på eksterne prosjekter og internt for å videreutvikle selskapet.

