INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (866, NOW(), 89, 'Internship som utvikler', 'Få en drømmestart på karrieren din med et internship i Gjensidige!', 'I løpet av 200 år har vi bidratt til å løse et viktig samfunnsoppdrag ved å sikre liv, helse og verdier for kundene våre. I Gjensidige engasjerer vi oss, og målet vårt er å bli Nordens mest kundeorienterte forsikringsselskap. Og hva trenger vi for å klare dette? Jo, dyktige lagspillere. Derfor søker vi nå nysgjerrige, nytenkende og løsningsorienterte studenter som ønsker seg praktisk og relevant erfaring innen Utvikling.

