INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1045, NOW(), 268, 'Embedded Developer - part-time', 'We want to reach out to a student that is passionate about developing on embedded platform and could be interested in part time/summer job at Nordic.', 'Nordic is exploring an open-source project originating from Google called PebbleOS. PebbleOS is a real time operating system (RTOS) designed to Pebble Smart Watches, which became a part of Googles FitBit acquisition in 2021. The project is built around FreeRTOS and already supports the Nordic nRF52840 device. We aim to extend this support to include ournRF54L series microcontrollers.

