INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1015, NOW(), 112, 'Cyber Trainee', 'Do you want to kickstart your career in Cyber Security? DNV is looking for cyber trainees
