INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (898, NOW(), 292, 'Holte Consulting søker sommerstudenter', 'Vil du ha tettere oppfølging, mer ansvar og mer læring enn mange andre sommerstudenter? Som konsulent i Holte Consulting jobber du med prosjekt-, team- og produktledelse innen enten IT-, energi-, anlegg- eller byggeprosjekter.', 'Vil du ha tettere oppfølging, mer ansvar og mer læring enn mange andre sommerstudenter? Dette er tre fordeler med å være sommerstudent hos oss i Holte Consulting. Hos oss er du en av få, ikke en av mange.

