INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (910, NOW(), 267, 'Engineering Summer Internship 2024', 'Every summer, Disruptive Technologies (DT) invites talented students to join us for an 8-10
week program in our Oslo!', '**Engineering Summer Internship 2024**

