INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (1023, NOW(), 295, 'Summer Intern 2025, Hovedkontoret', 'Kunne du tenke deg en sommer der du er med og bidrar til å gjøre en liten forskjell i verden og samtidig gi deg selv en flying start på egen karriere? Avinors sommerprogram 2025 er klar for din søknad. Velkommen til oss!', '# **Sommeren er best hos Avinor – bli med å utvikle fremtidens luftfart!**

