INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (937, NOW(), 296, 'Summer intern – utviklere', 'Vi er et teknologikonsern som leverer egenutviklet software til over 55.000 private- og offentlige bygg, og 3000 dagligvarebutikker i Europa. Fra 2001 til i dag vi gått fra å være en gründerbedrift i Trondheim til å bli del av et internasjonalt konse', 'Vi er et teknologikonsern som leverer egenutviklet software til over 55.000 private- og offentlige bygg, og 3000 dagligvarebutikker i Europa. Fra 2001 til i dag vi gått fra å være en gründerbedrift i Trondheim til å bli del av et internasjonalt konsern med kontorer i 8 land og 160 ansatte.

