INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (951, NOW(), 205, 'Backend-utvikler til godt betalt deltidsjobb', 'Kunne du tenkt deg en relevant, godt betalt og spennende deltidsjobb ved siden av studiene?', 'Wiseline ønsker å videreutvikle sine løsninger for kundenære, nettbaserte applikasjoner. Et team av studenter fra NTNU har siden august 2020 gjennomført et utviklingsprosjekt med fokus på utforsking og testing av teknologiplattform, funksjonalitet og brukergrensesnitt.

