INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (949, NOW(), 303, 'Zedge android summer project internship', 'Vi ser etter Bachelor eller Master studenter som ønsker å jobbe med Android utvikling sommeren 2024.  Sommerstudentene i Zedge jobber sammen i et team der oppgaven er å lage en Android app fra idè til fungerende pilot-produkt.', 'Vi ser etter Bachelor eller Master studenter som ønsker å jobbe med Android utvikling sommeren 2024. Sommerstudentene i Zedge jobber sammen i et team der oppgaven er å lage en Android app fra idè til fungerende pilot-produkt.  **For mer informasjon om prosjektet for sommeren 2024 - se vedlegg.**

