INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (963, NOW(), 243, 'Deltidsjobb som IT-konsulent', 'Append Consulting er et ungt, studentdrevet IT-konsulentselskap som spesialiserer seg innen systemutvikling og data science. På kort tid har vi gått fra nyoppstartet selskap til en viktig leverandør for selskaper som Helsedirektoratet og Norsk Hydro.', '**Vi søker studenter fra alle årstrinn!**

  
